@objc public protocol AFSEViewControllerProtocol: NSObjectProtocol {

    /// The container stack view of the view controller
    var containerStackView: ContainerStackViewProtocol? { get }

    /// The background image view of the view controller
    var backgroundImageView: UIImageView! { get set }

    /// The desired distance between the navigation items, used with the helper methods bellow.
    /// The default is 24.0f pt.
    var desiredDistanceBetweenNavigationbarItems: CGFloat { get set }

    /// The desired distance between the last(first) item and the end of the navigation bar,
    /// used with the helper methods bellow.
    /// The default is 10.0 pt.
    var desiredDistanceBetweenLastItemAndNavigationBar: CGFloat { get set }

    /// The default background image. Subclasses can set their own or nil to remove the background.
    var backgroundImage: UIImage? { get set }

    /// Touching non UITextField views results in keyboard being dismissed
    /// Default value is NO
    var touchesInBackgroundDismissKeyboard: Bool { get set }

    /// define if the keyboard diss/appearance should be handled
    var shouldHandleKeyboard: Bool { get set }

    /// Let self know whether controller is presented modaly
    func isModal() -> Bool
